mapper.select:
SELECT qna_no, qna_title, qna_category, TO_CHAR(qna_date, 'YYYY/MM/DD') qna_date, qna_category, qna_content
FROM qna_board
WHERE qna_no = #{qna_no}

mapper.update:
UPDATE qna_board
SET qna_category = #{qna_category}, qna_title=#{qna_title}, qna_content = #{qna_content}
WHERE qna_no = #{qna_no}
